import React, { useState, useEffect } from 'react';
import { FaBrain, FaEthereum, FaRobot, FaShieldAlt } from 'react-icons/fa';
import VideoPlayer, { VideoPlaceholder, EnhancedVideoPlayer } from './VideoPlayer';

const projects = [
  {
    id: 'deepfake',
    title: 'Deep Fake Detection Using CNN',
    description: 'Developed a convolution Neural Network (CNN) based project to identify AI-generated or edited images, enhancing digital content authenticity. Focused on image processing and deep learning techniques for robust detection.',
    icon: <FaBrain />,
    background: 'linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)',
    image: '/project_images/deepfake_cnn.png',
    summary: 'This project implements a deep learning model for detecting deepfake images using TensorFlow and Keras. The model is trained to classify images as either real or fake, utilizing a dataset of fake and real images. Implemented using Google Colab for faster training.',
    videoConfig: {
      hasMarkdown: true,
      features: ['CNN Architecture', 'TensorFlow/Keras', 'Real-time Detection', 'Web Interface'],
      duration: 30
    }
  },
  {
    id: 'cybersecurity',
    title: 'Real-Time Threat Intelligence System',
    description: 'Developed a cybersecurity project focused on real-time threat intelligence for organization security. Utilized OSINT tools to monitor threats and integrated Machine Learning models to accelerate the threat mitigation process.',
    icon: <FaShieldAlt />,
    background: 'linear-gradient(135deg, #8360c3 0%, #2ebf91 100%)',
    image: '/project_images/security_project.png',
    summary: 'A cybersecurity project focused on real-time threat intelligence, integrating OSINT tools like VirusTotal and HIBP, and leveraging LLMs for automated risk scoring, aligning with NIST CSF 2.0.',
    videoConfig: {
      hasMarkdown: true,
      features: ['OSINT Integration', 'NIST Framework', 'AI Risk Assessment', 'Real-time Monitoring'],
      duration: 35
    }
  },
  {
    id: 'blockchain',
    title: 'Blockchain-Based Real Estate Management System',
    description: 'Designed and implemented a secure real estate management system leverage blockchain technology. Enabled all real estate transactions to be conducted using Ethereum coins, ensuring transparency and immutability.',
    icon: <FaEthereum />,
    background: 'linear-gradient(135deg, #00c6fb 0%, #005bea 100%)',
    image: '/project_images/blockchain.png',
    summary: 'A secure real estate management system leveraging blockchain technology. All transactions are conducted using Ethereum coins, ensuring transparency and immutability.',
    videoConfig: {
      hasMarkdown: false,
      features: ['Blockchain Technology', 'Ethereum Integration', 'Smart Contracts', 'Transaction Security'],
      duration: 25
    }
  },
  {
    id: 'ai_agent',
    title: 'AI Call Agent With RAG Strategy',
    description: 'Created an AI Call Agent capable of interacting with callers naturally. Implemented a Retrieval-Augmented Generation (RAG) strategy within an Agentic AI system to process queries and identify important calls, forwarding them automatically to the user when necessary.',
    icon: <FaRobot />,
    background: 'linear-gradient(135deg, #ff7e5f 0%, #feb47b 100%)',
    image: 'https://via.placeholder.com/300x200/000000/FFFFFF?text=AI+Agent+Architecture',
    summary: 'An AI Call Agent designed for natural interaction, utilizing a Retrieval-Augmented Generation (RAG) strategy to process queries and identify important calls for automatic forwarding.',
    videoConfig: {
      hasMarkdown: false,
      features: ['RAG Strategy', 'Natural Language Processing', 'Call Automation', 'AI Agent Framework'],
      duration: 30
    }
  },
];

function Projects() {
  const [hoveredProject, setHoveredProject] = useState(null);
  const [videoData, setVideoData] = useState({});
  const [videoMode, setVideoMode] = useState('placeholder'); // 'placeholder', 'generated', 'ai_generated'

  // Load video data if available
  useEffect(() => {
    const loadVideoData = async () => {
      try {
        // Try to load pre-generated video data
        const response = await fetch('/src/data/all_video_data.json');
        if (response.ok) {
          const data = await response.json();
          setVideoData(data);
          setVideoMode('generated');
        }
      } catch (error) {
        console.log('No pre-generated video data found, using placeholders');
        setVideoMode('placeholder');
      }
    };

    loadVideoData();
  }, []);

  // Handle video generation trigger
  const handleGenerateVideos = async () => {
    try {
      setVideoMode('generating');

      // Import and run the markdown processing script
      const { processAllProjects } = await import('../scripts/processMarkdownForVideos.js');
      const generatedData = await processAllProjects();

      setVideoData(generatedData);
      setVideoMode('generated');

      console.log('✅ Video scripts generated successfully!');
    } catch (error) {
      console.error('❌ Error generating video scripts:', error);
      setVideoMode('placeholder');
    }
  };

  const renderProjectVisual = (project, index) => {
    const isHovered = hoveredProject === index;
    const projectVideoData = videoData[project.id];

    switch (videoMode) {
      case 'generated':
      case 'ai_generated':
        // Show video player with generated content
        return (
          <EnhancedVideoPlayer
            projectId={project.id}
            videoSrc={projectVideoData?.videoSrc}
            posterImage={project.image}
            isHovered={isHovered}
            videoData={{
              title: project.title,
              features: project.videoConfig.features,
              script: projectVideoData?.script
            }}
            showControls={isHovered}
          />
        );

      case 'generating':
        // Show loading state
        return (
          <div className="video-generating">
            <div className="generating-spinner"></div>
            <p>Generating video script...</p>
          </div>
        );

      case 'placeholder':
      default:
        // Show enhanced placeholder with project info
        if (project.videoConfig.hasMarkdown) {
          return (
            <VideoPlaceholder
              projectId={project.id}
              title={project.title}
              features={project.videoConfig.features}
            />
          );
        } else {
          // Fallback to original image for projects without markdown
          return (
            <img 
              src={project.image} 
              alt={project.title} 
              className="project-visual"
              style={{
                width: '100%',
                height: '200px',
                objectFit: 'cover',
                borderRadius: '12px'
              }}
            />
          );
        }
    }
  };

  return (
    <section id="projects">
      <div className="projects-header">
        <h2>Projects</h2>

        {/* Video Generation Controls */}
        <div className="video-controls-header">
          {videoMode === 'placeholder' && (
            <button 
              className="generate-videos-btn"
              onClick={handleGenerateVideos}
              title="Generate video content from project documentation"
            >
              🎬 Generate Video Previews
            </button>
          )}

          {videoMode === 'generated' && (
            <div className="video-status">
              <span className="status-indicator success">✅</span>
              Video previews ready
            </div>
          )}

          {videoMode === 'generating' && (
            <div className="video-status">
              <span className="status-indicator loading">⏳</span>
              Generating video content...
            </div>
          )}
        </div>
      </div>

      <div className="projects-grid">
        {projects.map((project, index) => (
          <div
            className="project-card"
            key={index}
            style={{ background: project.background }}
            onMouseEnter={() => setHoveredProject(index)}
            onMouseLeave={() => setHoveredProject(null)}
          >
            {/* Project Visual (Image or Video) */}
            <div className="project-visual-container">
              {renderProjectVisual(project, index)}
            </div>

            {/* Project Content */}
            <div className="project-card-content">
              {project.icon}
              <h3>{project.title}</h3>
              <p>{project.description}</p>

              {/* Enhanced hover content */}
              {hoveredProject === index && (
                <div className="project-enhanced-summary">
                  <div className="summary-content">
                    <p>{project.summary}</p>

                    {/* Show video script info if available */}
                    {videoData[project.id]?.script && (
                      <div className="video-info">
                        <small>
                          🎬 Video: {videoData[project.id].script.scenes.length} scenes, 
                          {videoData[project.id].script.totalDuration}s duration
                        </small>
                      </div>
                    )}

                    {/* Show key features */}
                    <div className="project-features">
                      {project.videoConfig.features.slice(0, 3).map((feature, idx) => (
                        <span key={idx} className="feature-pill">
                          {feature}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Usage Instructions */}
      <div className="video-instructions">
        <h3>🎥 Interactive Video Previews</h3>
        <p>
          Hover over project cards to see AI-generated video previews based on project documentation. 
          Videos are created from detailed markdown files covering implementation, features, and technical achievements.
        </p>

        {videoMode === 'placeholder' && (
          <div className="instructions-highlight">
            <p>
              <strong>Click "Generate Video Previews"</strong> to create dynamic video content from your 
              Deepfake Detection and Cybersecurity project documentation!
            </p>
          </div>
        )}
      </div>
    </section>
  );
}

export default Projects;
