import React, { useState, useRef, useEffect } from 'react';
import '../styles/VideoPlayer.css';

const VideoPlayer = ({ 
  projectId, 
  videoSrc, 
  posterImage, 
  isHovered, 
  onLoadStart,
  onLoadEnd,
  fallbackContent 
}) => {
  const [isLoading, setIsLoading] = useState(true);
  const [hasError, setHasError] = useState(false);
  const [isVideoReady, setIsVideoReady] = useState(false);
  const videoRef = useRef(null);

  useEffect(() => {
    if (isHovered && videoRef.current && isVideoReady && !hasError) {
      videoRef.current.currentTime = 0;
      videoRef.current.play().catch(err => {
        console.error('Error playing video:', err);
      });
    } else if (!isHovered && videoRef.current) {
      videoRef.current.pause();
    }
  }, [isHovered, isVideoReady, hasError]);

  const handleLoadStart = () => {
    setIsLoading(true);
    onLoadStart && onLoadStart();
  };

  const handleCanPlay = () => {
    setIsLoading(false);
    setIsVideoReady(true);
    onLoadEnd && onLoadEnd();
  };

  const handleError = () => {
    setHasError(true);
    setIsLoading(false);
    console.error(`Error loading video for project: ${projectId}`);
  };

  const handleLoadedData = () => {
    setIsVideoReady(true);
  };

  // If there's an error or no video source, show fallback
  if (hasError || !videoSrc) {
    return (
      <div className="video-fallback">
        {fallbackContent || (
          <div className="fallback-content">
            <div className="fallback-icon">🎬</div>
            <p>Video Preview</p>
            <small>Hover to see project details</small>
          </div>
        )}
      </div>
    );
  }

  return (
    <div className="video-container">
      {isLoading && (
        <div className="video-loading">
          <div className="loading-spinner"></div>
          <p>Loading video...</p>
        </div>
      )}

      <video
        ref={videoRef}
        className={`project-video ${isLoading ? 'loading' : ''} ${isHovered ? 'playing' : ''}`}
        poster={posterImage}
        muted
        loop
        playsInline
        preload="metadata"
        onLoadStart={handleLoadStart}
        onCanPlay={handleCanPlay}
        onLoadedData={handleLoadedData}
        onError={handleError}
      >
        <source src={videoSrc} type="video/mp4" />
        <source src={videoSrc.replace('.mp4', '.webm')} type="video/webm" />
        Your browser does not support the video tag.
      </video>

      {/* Video overlay for better visual effect */}
      <div className={`video-overlay ${isHovered ? 'visible' : ''}`}>
        <div className="play-indicator">
          <div className="play-icon">▶</div>
        </div>
      </div>
    </div>
  );
};

// Enhanced Video Player with additional features
export const EnhancedVideoPlayer = ({ 
  projectId, 
  videoSrc, 
  posterImage, 
  isHovered,
  videoData,
  showControls = false 
}) => {
  const [showScript, setShowScript] = useState(false);

  return (
    <div className="enhanced-video-container">
      <VideoPlayer
        projectId={projectId}
        videoSrc={videoSrc}
        posterImage={posterImage}
        isHovered={isHovered}
        fallbackContent={
          <div className="enhanced-fallback">
            <div className="project-preview">
              <h4>{videoData?.title || 'Project Preview'}</h4>
              <div className="preview-features">
                {videoData?.features?.slice(0, 3).map((feature, idx) => (
                  <div key={idx} className="feature-tag">
                    {feature}
                  </div>
                ))}
              </div>
            </div>
          </div>
        }
      />

      {showControls && videoData?.script && (
        <div className="video-controls">
          <button 
            className="script-toggle"
            onClick={() => setShowScript(!showScript)}
          >
            {showScript ? 'Hide' : 'Show'} Script
          </button>

          {showScript && (
            <div className="video-script">
              <h5>Video Script:</h5>
              {videoData.script.scenes.map((scene, idx) => (
                <div key={idx} className="script-scene">
                  <strong>Scene {idx + 1}:</strong> {scene.voiceover}
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

// Video placeholder component for development
export const VideoPlaceholder = ({ projectId, title, features = [] }) => {
  return (
    <div className="video-placeholder">
      <div className="placeholder-content">
        <div className="placeholder-icon">🎥</div>
        <h4>{title}</h4>
        <div className="placeholder-features">
          {features.slice(0, 2).map((feature, idx) => (
            <span key={idx} className="feature-badge">
              {feature}
            </span>
          ))}
        </div>
        <small>Video generated from project documentation</small>
      </div>
      <div className="placeholder-animation">
        <div className="pulse-circle"></div>
      </div>
    </div>
  );
};

export default VideoPlayer;
